/*
const singleSchema = single.object({
  partnerName: single.string().required(),
  releaseDate: single.string().required(),
  album: single.string().required(),
  genre: single.string().required(),
  upc: single.string().required(),
  publisher: single.string().required(),
  publishingYear: single.string().required(),
  customId: single.string().required(),
})
