            id="username"
            name="username"
            type="text"
            data-parse="uppercase"
          />

          <label htmlFor="email">Email:</label>
          <input id="email" name="email" type="email" required />

          <label htmlFor="birthdate">Birthdate:</label>
          <input
            id="birthdate"
            name="birthdate"
            type="text"
            data-parse="date"
            placeholder="MM/DD//YYYY"
            pattern="\d{2}\/\d{2}/\d{4}"
