/*!

=========================================================
* Light Bootstrap Dashboard React - v1.3.0
=========================================================

* Product Page: https://www.creative-tim.com/product/light-bootstrap-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/light-bootstrap-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from "react";
import {
  Grid,
  Row,
  Col,
  FormGroup,
  ControlLabel,
  FormControl
} from "react-bootstrap";

import { TextField } from '@material-ui/core';

import { makeStyles } from '@material-ui/core/styles';
import FilledInput from '@material-ui/core/FilledInput';
import FormHelperText from '@material-ui/core/FormHelperText';
//import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import OutlinedInput from '@material-ui/core/OutlinedInput';

import { Card } from "components/Card/Card.jsx";
import { FormInputs } from "components/FormInputs/FormInputs.jsx";
import { UserCard } from "components/UserCard/UserCard.jsx";
import Radio from  "components/CustomRadio/CustomRadio.jsx";
//import Button from "components/CustomButton/CustomButton.jsx";
import Select from "components/Select/Select.jsx";
import Button from "components/Button/Button.jsx";
import Input from "components/Input/Input.jsx";

import avatar from "assets/img/faces/face-3.jpg";

/*
function stringifyFormData(fd) {
  const data = {};
	for (let key of fd.keys()) {
  	data[key] = fd.get(key);
  }
  return JSON.stringify(data, null, 2);
}
*/

class UploadRelease extends Component {
  constructor(props) {
    super(props);

    this.state = {
      newReleaseContent: {
        songTitle: "",
        releaseYear: "",
        partnerName: ""
        //skills: [],
        //about: ""
      },

      partnerOptions: ["300 Entertainment", "The Royt Sound", "Others"],
      //skillOptions: ["Programming", "Development", "Design", "Testing"]
    };
    this.handleReleaseYear = this.handleReleaseYear.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.handleClearForm = this.handleClearForm.bind(this);
    this.handleInput = this.handleInput.bind(this);
  }

  handleReleaseYear(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newReleaseContent: {
          ...prevState.newReleaseContent,
          releaseYear: value
        }
      }),
      () => console.log(this.state.newReleaseContent)
    );
  }

  handleInput(e) {
    let value = e.target.value;
    let name = e.target.name;
    this.setState(
      prevState => ({
        newReleaseContent: {
          ...prevState.newReleaseContent,
          [name]: value
        }
      }),
      () => console.log(this.state.newReleaseContent)
    );
  }

  handleFormSubmit(e) {
    e.preventDefault();
    let releaseData = this.state.newReleaseContent;

    fetch("http://localhost:8080/uploadLive", {
      method: "POST",
      mode: "no-cors",
      body: JSON.stringify(releaseData),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    }).then(response => {
      response.json().then(data => {
        console.log("Successful" + data);
      });
    }).catch(error => {
        alert(error.message);
    });
  }

  handleClearForm(e) {
    e.preventDefault();
    this.setState({
      newReleaseContent: {
        songTitle: "",
        releaseYear: "",
        partnerName: ""
      }
    });
  }

  render() {
    return (
      <form className="container-fluid" onSubmit={this.handleFormSubmit}>
        <Input
          inputType={"text"}
          title={"Song Title"}
          name={"songTitle"}
          value={this.state.newReleaseContent.songTitle}
          placeholder={"Enter the song title"}
          handleChange={this.handleInput}
        />{" "}
        {/* Name of the user */}
        <Input
          inputType={"number"}
          name={"releaseYear"}
          title={"Release Year"}
          value={this.state.newReleaseContent.releaseYear}
          placeholder={"Enter the release year"}
          handleChange={this.handleReleaseYear}
        />{" "}
        {/* Age */}
        <Select
          title={"Partner Name"}
          name={"partnerName"}
          options={this.state.partnerOptions}
          value={this.state.newReleaseContent.partnerName}
          placeholder={"Select Partner"}
          handleChange={this.handleInput}
        />{" "}
        <Button
          action={this.handleFormSubmit}
          type={"primary"}
          title={"Submit"}
          style={buttonStyle}
        />{" "}
        {/*Submit */}
        <Button
          action={this.handleClearForm}
          type={"secondary"}
          title={"Clear"}
          style={buttonStyle}
        />{" "}
        {/* Clear the form */}
      </form>
    );
  }
}

const buttonStyle = {
  margin: "10px 10px 10px 10px"
};

export default UploadRelease;
