/*!

=========================================================
* Light Bootstrap Dashboard React - v1.3.0
=========================================================

* Product Page: https://www.creative-tim.com/product/light-bootstrap-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/light-bootstrap-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from "react";
import {
  Grid,
  Row,
  Col,
  FormGroup,
  ControlLabel,
  FormControl
} from "react-bootstrap";

import { Card } from "components/Card/Card.jsx";
import { FormInputs } from "components/FormInputs/FormInputs.jsx";
import { UserCard } from "components/UserCard/UserCard.jsx";
import Radio from  "components/CustomRadio/CustomRadio.jsx";
import Button from "components/CustomButton/CustomButton.jsx";

import avatar from "assets/img/faces/face-3.jpg";

/*
fetch('https://localhost:8000/uploadLive/', {
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    firstParam: 'yourValue',
    secondParam: 'yourOtherValue'
  })
});
*/



/*
const singleSchema = single.object({
  partnerName: single.string().required(),
  releaseDate: single.string().required(),
  album: single.string().required(),
  genre: single.string().required(),
  upc: single.string().required(),
  publisher: single.string().required(),
  publishingYear: single.string().required(),
  customId: single.string().required(),
})
*/

class UploadRelease extends Component {

  constructor() {
      super();
      this.handleSubmit = this.handleSubmit.bind(this);

  }
  /*
  handleRadio (e) {
    e.preventDefault();
    console.log("RADIO BUTTON WAS PRESSED");
    const target = e.target;
    this.setState({
        [target.name]: target.value
    });
  };
  */
  handleSubmit (e) {
    e.preventDefault();
    console.log("SUBMIT BUTTON WAS PRESSED");

    const data = new FormData(e.target);


  }

  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col md={8}>
              <Card
                title="Edit Release "
                content={
                  <form onSubmit={this.handleSubmit}>
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "Partner Name",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Partner Name",
                          defaultValue: "300 Entertainment",
                          disabled: true
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      onChange={this.handleClick}
                      properties={[
                        {
                          label: "Release Date",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Release Date",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "Album",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Album Name",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "Genre",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Genre",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "UPC",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "UPC",
                          defaultValue: ""
                        }
                     ]}
                     />
                     <FormInputs
                       ncols={["col-md-6"]}
                       properties={[
                        {
                          label: "Publisher",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Publisher",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "Publishing Year",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Publishing Year",
                          defaultValue: ""
                        }
                      ]}
                   />
                   <FormInputs
                     ncols={["col-md-6"]}
                     properties={[
                        {
                          label: "Custom ID",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Custom ID",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <Button bsStyle="info" pullRight fill type="submit">
                      Upload Release
                    </Button>
                    <div className="clearfix" />
                  </form>
                }
              />
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

export default UserProfile;
