    return TIMELINE_CLASS;
  },
  _getCellCount: function _getCellCount() {
    return this.callBase() * this._getWeekDuration();
  },
  _getHeaderPanelCellWidth: function _getHeaderPanelCellWidth($headerRow) {
    return getBoundingRect($headerRow.children().first().get(0)).width;
  },
  _getWeekDuration: function _getWeekDuration() {
    return 7;
  },
  _needRenderWeekHeader: function _needRenderWeekHeader() {
    return true;
  },
