*/
import React, { Component } from "react";
import {
  Grid,
  Row,
  Col,
  FormGroup,
  ControlLabel,
  FormControl
} from "react-bootstrap";

import { Card } from "components/Card/Card.jsx";
import { FormInputs } from "components/FormInputs/FormInputs.jsx";
import { UserCard } from "components/UserCard/UserCard.jsx";
import Radio from  "components/CustomRadio/CustomRadio.jsx";
import Button from "components/CustomButton/CustomButton.jsx";

import avatar from "assets/img/faces/face-3.jpg";

/*
fetch('https://localhost:8000/uploadLive/', {
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    firstParam: 'yourValue',
    secondParam: 'yourOtherValue'
  })
});
*/



/*
const singleSchema = single.object({
  partnerName: single.string().required(),
  releaseDate: single.string().required(),
  album: single.string().required(),
  genre: single.string().required(),
  upc: single.string().required(),
  publisher: single.string().required(),
  publishingYear: single.string().required(),
  customId: single.string().required(),
})
*/

class ShakingError extends React.Component {
	constructor() { super(); this.state = { key: 0 }; }

	componentWillReceiveProps() {
    // update key to remount the component to rerun the animation
  	this.setState({ key: ++this.state.key });
  }

  render() {
  	return <div key={this.state.key} className="bounce">{this.props.text}</div>;
  }
}

class UploadRelease extends Component {

  constructor() {
      super();
      this.state = {};
      this.handleSubmit = this.handleSubmit.bind(this);

  }
  /*
  handleRadio (e) {
    e.preventDefault();
    console.log("RADIO BUTTON WAS PRESSED");
    const target = e.target;
    this.setState({
        [target.name]: target.value
    });
  };
  */
  handleSubmit (e) {
    e.preventDefault();
    console.log("SUBMIT BUTTON WAS PRESSED");

    const form = e.target;
    const data = new FormData(form);

    for (let name of data.keys()) {
      const input = form.elements[name];
      const parserName = input.dataset.parse;
      console.log('parser name is', parserName);
      if (parserName) {
        const parsedValue = inputParsers[parserName](data.get(name))
        data.set(name, parsedValue);
      }
    }

    this.setState({
    	res: stringifyFormData(data),
      invalid: false,
      displayErrors: false,
    });

  }

  render() {
    const { res, invalid, displayErrors } = this.state;
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col md={8}>
              <Card
                title="Edit Release "
                content={
                  <form
                  onSubmit={this.handleSubmit}
                  noValidate
                  className={displayErrors ? 'displayErrors' : ''}
                  >
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "Partner Name",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Partner Name",
                          defaultValue: "300 Entertainment",
                          disabled: true
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      onChange={this.handleClick}
                      properties={[
                        {
                          label: "Release Date",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Release Date",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "Album",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Album Name",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "Genre",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Genre",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "UPC",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "UPC",
                          defaultValue: ""
                        }
                     ]}
                     />
                     <FormInputs
                       ncols={["col-md-6"]}
                       properties={[
                        {
                          label: "Publisher",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Publisher",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <FormInputs
                      ncols={["col-md-6"]}
                      properties={[
                        {
                          label: "Publishing Year",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Publishing Year",
                          defaultValue: ""
                        }
                      ]}
                   />
                   <FormInputs
                     ncols={["col-md-6"]}
                     properties={[
                        {
                          label: "Custom ID",
                          type: "text",
                          bsClass: "form-control",
                          placeholder: "Custom ID",
                          defaultValue: ""
                        }
                      ]}
                    />
                    <Button bsStyle="info" pullRight fill type="submit">
                      Upload Release
                    </Button>
                    <div className="clearfix" />
                  </form>
                }
              />
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

export default UploadRelease;
